mapper.update:
UPDATE biz_system_reserve_summary
SET total_reserve_amount = total_reserve_amount + #{amount}
WHERE summary_id = #{id}

UPDATE biz_system_reserve_summary
SET total_reserve_amount = total_reserve_amount - #{amount}
WHERE summary_id = #{id}

UPDATE biz_system_reserve_summary
SET total_reserve_amount = total_reserve_amount - #{amount}
WHERE summary_id = 1 AND total_reserve_amount >= #{amount}
